operation.add:
//lazada component
$warehouse = array();

if ($this->user->hasPermission('access', 'warehouse/setting')) {
    $warehouse[] = array(
        'name'     => 'warehouse Setting',
        'href'     => $this->url->link('warehouse/setting', 'user_token=' . $this->session->data['user_token'], true),
        'children' => array()       
    );
}

if ($warehouse) {
    $data['menus'][] = array(
        'id'       => 'menu-catalog',
        'icon'     => 'fa-tags', 
        'name'     => 'Warehouse Manager',
        'href'     => '',
        'children' => $warehouse
    );
}
